import React from "react";

function Heading(){
    const date = new Date();
    const currTime = date.getHours();

    let wish;

    const customStyle = {
        color : ''
    }

    if(currTime < 12){
        wish = 'Good Morning';
        customStyle.color = 'red';
    }else if(currTime < 18){
        wish = 'Good Afternoon';
        customStyle.color = 'yellow'
    }else{
        wish = 'Good Night';
        customStyle.color = 'blue'
    }
    return (
        <div>
            <h1 style={customStyle}> {wish} </h1>
        </div>
    )
}

export default Heading;
