import React from "react";
import ReactDOM  from "react-dom";

const date = new Date();
const currTime = date.getHours();

let wish;

const customStyle = {
  color : ''
}

if(currTime < 12){
  wish = 'Good Morning';
  customStyle.color = 'Red';
}else if(currTime < 18){
  wish = 'Good Afternoon';
  customStyle.color = 'Yellow'
}else{
  wish = 'Good Night';
  customStyle.color = 'violet'
}

ReactDOM.render(<h1 color = {customStyle} >Hello World </h1>, document.getElementById('root'));
