import React from "react";
import ReactDOM  from "react-dom";

const date = new Date(2001,6,5,13);
const currTime = date.getHours();

let wish;

const customStyle = {
  color : ''
}

if(currTime < 12){
  wish = 'Good Morning';
  customStyle.color = 'red';
}else if(currTime < 18){
  wish = 'Good Afternoon';
  customStyle.color = 'yellow'
}else{
  wish = 'Good Night';
  customStyle.color = 'blue'
}

ReactDOM.render(<h1 style = {customStyle} > {wish} </h1>, document.getElementById('root'));
