import React from "react";
import ReactDOM  from "react-dom";

const date = new Date(2023,6,5,);
const currTime = date.getHours();

let wish;

const customStyle = {
  color : ''
}

if(currTime < 12){
  wish = 'Good Morning';
  customStyle.color = 'red';
}else if(currTime < 18){
  wish = 'Good Afternoon';
  customStyle.color = 'yellow'
}else{
  wish = 'Good Night';
  customStyle.color = 'brinjal'
}

ReactDOM.render(<h1 color = {customStyle} > {wish} </h1>, document.getElementById('root'));
